import React, { useState } from 'react';

function Notes({ notes, setNotes, username }) {  // Add setNotes to props
    const [newNote, setNewNote] = useState('');

    const addNote = async () => {
        const response = await fetch('http://localhost:8000/api/add_note/', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ username, note: newNote })
        });

        const data = await response.json();
        if (response.ok) {
            setNotes(prevNotes => [...prevNotes, data.note]);  // Update the notes state with the newly added note
            alert(data.message || 'Note added successfully');
            setNewNote(''); // Clear the input field after adding the note
        } else {
            alert(data.error);
        }
    };

    return (
        <div>
            <h2>Your Notes</h2>
            <ul>
                {notes.map(note => (
                    <li key={note.id}>{note.content} (Created at: {note.timestamp})</li>
                ))}
            </ul>
            <input 
                type="text" 
                placeholder="New note" 
                value={newNote}
                onChange={e => setNewNote(e.target.value)} 
            />
            <button onClick={addNote}>Add Note</button>
        </div>
    );
}

export default Notes;
