import React, { useState } from 'react';
import Register from './Register';
import Login from './Login';
import Notes from './Notes';

function App() {
    const [notes, setNotes] = useState([]);
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState(''); // State for logged-in username

    // Function to handle logout
    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setNotes([]);
    };

    return (
        <div className="App">
            {!isLoggedIn ? (
                <>
                    <Register />
                    <Login setNotes={setNotes} setIsLoggedIn={setIsLoggedIn} setUsername={setUsername} />
                </>
            ) : (
                <div>
                    <button onClick={handleLogout}>Logout</button>
                    <Notes notes={notes} setNotes={setNotes} username={username} />  {/* Pass setNotes as a prop */}
                </div>
            )}
        </div>
    );
}

export default App;
